mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, simple_class_name, type, super_itf_class_name, seq, sign_class_name, class_name
from lv_invoke_class_signature_ei1


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and simple_class_name = #{simpleClassName}


        and type = #{type}


        and super_itf_class_name = #{superItfClassName}


        and seq = #{seq}


        and sign_class_name = #{signClassName}


        and class_name = #{classNameDo}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_class_signature_ei1


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and simple_class_name = #{simpleClassName}


        and type = #{type}


        and super_itf_class_name = #{superItfClassName}


        and seq = #{seq}


        and sign_class_name = #{signClassName}


        and class_name = #{classNameDo}
