mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, simple_class_name, class_name, access_flags, type, seq, exists_downward_classes, upward_simple_class_name, upward_class_name
from lv_invoke_extends_impl


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and simple_class_name = #{simpleClassName}


        and class_name = #{classNameDo}


        and access_flags = #{accessFlags}


        and type = #{type}


        and seq = #{seq}


        and exists_downward_classes = #{existsDownwardClasses}


        and upward_simple_class_name = #{upwardSimpleClassName}


        and upward_class_name = #{upwardClassName}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_extends_impl


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and simple_class_name = #{simpleClassName}


        and class_name = #{classNameDo}


        and access_flags = #{accessFlags}


        and type = #{type}


        and seq = #{seq}


        and exists_downward_classes = #{existsDownwardClasses}


        and upward_simple_class_name = #{upwardSimpleClassName}


        and upward_class_name = #{upwardClassName}
