mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, jar_type, jar_path_hash, jar_full_path, jar_file_name, last_modified, jar_hash
from lv_invoke_jar_info


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and jar_type = #{jarType}


        and jar_path_hash = #{jarPathHash}


        and jar_full_path = #{jarFullPath}


        and jar_file_name = #{jarFileName}


        and last_modified = #{lastModified}


        and jar_hash = #{jarHash}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_jar_info


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and jar_type = #{jarType}


        and jar_path_hash = #{jarPathHash}


        and jar_full_path = #{jarFullPath}


        and jar_file_name = #{jarFileName}


        and last_modified = #{lastModified}


        and jar_hash = #{jarHash}
