mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, method_hash, annotation_name, attribute_name, attribute_type, attribute_value, full_method, simple_class_name, spring_mapping_annotation
from lv_invoke_method_annotation


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and annotation_name = #{annotationName}


        and attribute_name = #{attributeName}


        and attribute_type = #{attributeType}


        and attribute_value = #{attributeValue}


        and full_method = #{fullMethod}


        and simple_class_name = #{simpleClassName}


        and spring_mapping_annotation = #{springMappingAnnotation}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_method_annotation


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and annotation_name = #{annotationName}


        and attribute_name = #{attributeName}


        and attribute_type = #{attributeType}


        and attribute_value = #{attributeValue}


        and full_method = #{fullMethod}


        and simple_class_name = #{simpleClassName}


        and spring_mapping_annotation = #{springMappingAnnotation}
