mapper.select:
select
  project_id, create_time, update_time, is_deleted, method_hash, arg_seq, simple_arg_type, arg_type, simple_class_name, full_method
from lv_invoke_method_arg_type


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and arg_seq = #{argSeq}


        and simple_arg_type = #{simpleArgType}


        and arg_type = #{argType}


        and simple_class_name = #{simpleClassName}


        and full_method = #{fullMethod}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_method_arg_type


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and arg_seq = #{argSeq}


        and simple_arg_type = #{simpleArgType}


        and arg_type = #{argType}


        and simple_class_name = #{simpleClassName}


        and full_method = #{fullMethod}
