mapper.select:
select
  project_id, create_time, update_time, is_deleted, call_id, obj_args_seq, seq, type, array_flag, the_value
from lv_invoke_method_call_info


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and call_id = #{callId}


        and obj_args_seq = #{objArgsSeq}


        and seq = #{seq}


        and type = #{type}


        and array_flag = #{arrayFlag}


        and the_value = #{theValue}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_method_call_info


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and call_id = #{callId}


        and obj_args_seq = #{objArgsSeq}


        and seq = #{seq}


        and type = #{type}


        and array_flag = #{arrayFlag}


        and the_value = #{theValue}
