mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, method_hash, simple_class_name, access_flags, method_name, full_method, simple_return_type, return_type
from lv_invoke_method_info


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and simple_class_name = #{simpleClassName}


        and access_flags = #{accessFlags}


        and method_name = #{methodName}


        and full_method = #{fullMethod}


        and simple_return_type = #{simpleReturnType}


        and return_type = #{returnType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_method_info


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and simple_class_name = #{simpleClassName}


        and access_flags = #{accessFlags}


        and method_name = #{methodName}


        and full_method = #{fullMethod}


        and simple_return_type = #{simpleReturnType}


        and return_type = #{returnType}
