mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, method_hash, simple_class_name, min_line_number, max_line_number, full_method
from lv_invoke_method_line_number


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and simple_class_name = #{simpleClassName}


        and min_line_number = #{minLineNumber}


        and max_line_number = #{maxLineNumber}


        and full_method = #{fullMethod}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_method_line_number


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and simple_class_name = #{simpleClassName}


        and min_line_number = #{minLineNumber}


        and max_line_number = #{maxLineNumber}


        and full_method = #{fullMethod}
