mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, mapper_simple_class_name, mapper_method_name, sql_statement, table_seq, table_name, mapper_class_name
from lv_invoke_mybatis_ms_table


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and mapper_simple_class_name = #{mapperSimpleClassName}


        and mapper_method_name = #{mapperMethodName}


        and sql_statement = #{sqlStatement}


        and table_seq = #{tableSeq}


        and table_name = #{tableName}


        and mapper_class_name = #{mapperClassName}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_mybatis_ms_table


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and mapper_simple_class_name = #{mapperSimpleClassName}


        and mapper_method_name = #{mapperMethodName}


        and sql_statement = #{sqlStatement}


        and table_seq = #{tableSeq}


        and table_name = #{tableName}


        and mapper_class_name = #{mapperClassName}
