mapper.select:
select
  id, project_id, create_time, update_time, is_deleted, method_hash, seq, show_uri, class_path, method_path, annotation_name, simple_class_name, full_method
from lv_invoke_spring_controller


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and seq = #{seq}


        and show_uri = #{showUri}


        and class_path = #{classPath}


        and method_path = #{methodPath}


        and annotation_name = #{annotationName}


        and simple_class_name = #{simpleClassName}


        and full_method = #{fullMethod}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_spring_controller


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and method_hash = #{methodHash}


        and seq = #{seq}


        and show_uri = #{showUri}


        and class_path = #{classPath}


        and method_path = #{methodPath}


        and annotation_name = #{annotationName}


        and simple_class_name = #{simpleClassName}


        and full_method = #{fullMethod}
