mapper.select:
select
id, project_id, create_time, update_time, is_deleted, spring_bean_name, class_name, method_name
from lv_invoke_spring_task


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and spring_bean_name = #{springBeanName}


        and class_name = #{classNameDo}


        and method_name = #{methodName}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from lv_invoke_spring_task


        and id = #{id}


        and project_id = #{projectId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and spring_bean_name = #{springBeanName}


        and class_name = #{classNameDo}


        and method_name = #{methodName}
